"use strict";const h=(m,c)=>{try{const t=new Date(m).getTime()-new Date().getTime(),e=new Intl.RelativeTimeFormat(c,{numeric:"auto"}),n=Math.abs(t)/1e3>>0;if(n<60)return e.format(Math.sign(t)*n,"second");const r=n/60>>0;if(r<60)return e.format(Math.sign(t)*r,"minute");const s=r/60>>0;if(s<24)return e.format(Math.sign(t)*s,"hour");const a=s/24>>0;if(a<7)return e.format(Math.sign(t)*a,"day");const o=a/7>>0;if(o<4)return e.format(Math.sign(t)*o,"week");const i=o/4>>0;if(i<12)return e.format(Math.sign(t)*i,"months");const u=i/12>>0;return e.format(Math.sign(t)*u,"years")}catch{return null}};module.exports=h;
